Associated PHP Code:
<?php
mysql_connect("---server---", "---username---", "---password---") or die(mysql_error());
mysql_select_db("pageturntouch") or die(mysql_error());

$album_id = $_GET['a'];
$index = $_GET['i'];

//if $folder doesn't exist in mysql, create it, set index to zero
mysql_query("INSERT INTO pages (album_id) VALUES('$album_id') ");

//set the index
if ($index != -1){
mysql_query("UPDATE pages SET page=$index WHERE album_id='$album_id' ");
}

$result = mysql_query("SELECT * FROM pages WHERE album_id='$album_id' LIMIT 1");
$row = mysql_fetch_array( $result );

echo $row['page'];
//return the current index
?>

